import React, { useState, useEffect } from "react";

function useFetchBatters() {
  const [isLoading, setIsLoading] = useState(true)
  const [error, setError] = useState<null | string>(null)
  const [data, setData] = useState([])

  const fetchTopBatters = async(clearData:boolean = false)=>{
    setIsLoading(true)
    try {
      const response = await fetch("https://localhost:3001/api/scraper?clear=" + clearData)

      const data = await response.json() 
      setData(data)
    } catch(err){
      console.log("Error fetching top batters:" + err)
      setError("Error fetching top batters")
    } finally {
      setIsLoading(false)
    }
  }

}

export default useFetchBatters;
