import React, { useState, useEffect } from "react";

function useFetchBatters() {
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<null | string>(null);
  const [data, setData] = useState([]);

  const fetchBatters = async (clearData:boolean) => {
    setIsLoading(true);
    try {
      const response = await fetch(
        "http://localhost:3001/api/scraper?clear=" + clearData
      );
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      setData(data);
    } catch (error) {
      setError("Error fetching top batters");
      console.error("Error fetching top batters", error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchBatters();
  }, []);
  return { data, isLoading, error, refetch: fetchBatters };
}

export default useFetchBatters;
